export default function Options({ question, dispatch, answer }) {
  return (
    <div className="options">
      {question.incorrectAnswers.map +
        question.correctAnswer((option, index) => (
          <button className="btn btn-option" key={option}>
            {option}
          </button>
        ))}
    </div>
  );
}
