export default function Options({
  question,
  dispatch,
  answer,
  index,
  maxPoints,
}) {
  const answers = [...question.incorrectAnswers, question.correctAnswer].sort();
  if (index < maxPoints - 1)
    return (
      <div className="options">
        {answers.map((option, index) => (
          <button
            className={`btn-option ${answer === option ? "active" : ""}`}
            key={index}
            onClick={() => dispatch({ type: "newAnswer", payload: option })}
          >
            {/* <img
              src={answer === option ? "./images/Check_round_fill.svg" : ""}
              alt="pp"
            /> */}
            {option}
          </button>
        ))}
      </div>
    );
  if (index === maxPoints - 1)
    return <button onClick={() => dispatch({ type: "finish" })}>Finish</button>;
}
