import { act, useReducer } from "react";
import Header from "./Header";
import Main from "./Main";

const initialState = {
  questions: [],
  status: "loading",
};

function reducer(state, action) {
  switch (action.type) {
    case "dataRecieved": {
      return { ...state };
    }
    default: {
      throw new Error("Action unknown");
    }
  }
}

function App() {
  // const [state, dispatch] = useReducer(reducer, initialState);
  return (
    <div className="app">
      <Header />
      <Main></Main>
    </div>
  );
}

export default App;
